using namespace std;
    
    
    
    
    float factorial(float n){
        if (n==0)
        {return 1;
            /* code */
        }
        
        return n*factorial(n-1);
    }
    
    
    
    
    
    
int main()
{
 float n;
 cin>>n;
 // factorial using recursion

 cout<<factorial(n);
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
    return 0;
}